import React from 'react';

const Header = () => {
  return (
    <header className="bg-white shadow-sm border-b border-gray-200">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between items-center h-16">
          <div className="flex items-center">
            {/* Vaibhavam Logo */}
            <div className="h-10 w-10 mr-3 flex-shrink-0">
              <img
                src="/logo.png"
                alt="Vaibhavam Logo"
                className="h-10 w-10 object-contain"
                onError={(e) => {
                  // Fallback if image doesn't load
                  e.target.style.display = 'none';
                  e.target.nextSibling.style.display = 'flex';
                }}
              />
              {/* Fallback logo */}
              <div
                className="h-10 w-10 bg-gradient-to-br from-amber-400 to-amber-600 rounded-lg items-center justify-center text-white font-bold text-lg hidden"
              >
                V
              </div>
            </div>
            <div>
              <h1 className="text-xl font-bold text-gray-900">
                Vaibhavam Management System
              </h1>
              <p className="text-xs text-amber-600 font-medium">
                Restaurant Management Excellence
              </p>
            </div>
          </div>
          <div className="flex items-center space-x-4">
            <span className="text-sm text-gray-500">Welcome, Manager</span>
            <div className="h-8 w-8 bg-amber-500 rounded-full flex items-center justify-center">
              <span className="text-white font-semibold text-sm">M</span>
            </div>
          </div>
        </div>
      </div>
    </header>
  );
};

export default Header;
